import pandas as pd

df = pd.read_csv('source_files/MIMIC-IV.csv')

numerical_features = df.select_dtypes(include=['int64', 'float64']).columns

def calculate_max_min_ratio(df, feature):
    bins = 10
    df[feature + '_binned'] = pd.qcut(df[feature], q=bins, duplicates='drop')
    bin_counts = df[feature + '_binned'].value_counts()
    max_count = bin_counts.max()
    min_count = bin_counts.min()
    if min_count == 0:
        return 'Extreme Bias'
    else:
        ratio = max_count / min_count
        if ratio > 100:
            return 'Extreme Bias'
        elif ratio > 10:
            return 'Significant Bias'
        elif ratio > 5:
            return 'Moderate Bias'
        elif ratio > 2:
            return 'Minimal Bias'
        else:
            return 'No Bias'

bias_levels = {}
for feature in numerical_features:
    bias_level = calculate_max_min_ratio(df, feature)
    bias_levels[feature] = bias_level
print(bias_levels)

import pandas as pd
from scipy import stats

df = pd.read_csv('source_files/MIMIC-IV.csv')

categorical_features = df.select_dtypes(include=['object']).columns
numerical_features = df.select_dtypes(include=['int64', 'float64']).columns

def calculate_cohens_d(df, categorical_feature, numerical_feature):
    group1 = df[df[categorical_feature] == df[categorical_feature].unique()[0]][numerical_feature]
    group2 = df[df[categorical_feature] == df[categorical_feature].unique()[1]][numerical_feature]
    t_stat, p_val = stats.ttest_ind(group1, group2)
    cohen_d = (group1.mean() - group2.mean()) / ((group1.std() + group2.std()) / 2)
    return cohen_d

bias_levels = {}
for categorical_feature in categorical_features:
    for numerical_feature in numerical_features:
        if len(df[categorical_feature].unique()) == 2:
            cohen_d = calculate_cohens_d(df, categorical_feature, numerical_feature)
            if abs(cohen_d) > 1:
                bias_level = 'Extreme Bias'
            elif abs(cohen_d) > 0.5:
                bias_level = 'Significant Bias'
            elif abs(cohen_d) > 0.2:
                bias_level = 'Moderate Bias'
            elif abs(cohen_d) > 0.1:
                bias_level = 'Minimal Bias'
            else:
                bias_level = 'No Bias'
            bias_levels[(categorical_feature, numerical_feature)] = bias_level
print(bias_levels)
